<?phpnamespace app\common\model;class GemapayUser extends BaseModel{    public function addUser($telphone, $password)    {        $data['telphone'] = $telphone;        $data['password'] = md5($password);        $data['last_onlie_time'] = request()->time();        $data['desposit'] = 0;        return $this->insert($data);    }    public function checkUser($telphone)    {        $where['telphone'] = $telphone;        return $this->where($where)->find();    }    public function checkLogin($telphone, $password)    {        $where['telphone'] = $telphone;        $where['password'] = md5($password);        return $this->where($where)->find();    }    public function checkLoginByToken($telphone, $token)    {        $where['telphone'] = $telphone;        $where['token'] = $token;        return $this->where($where)->find();    }    public function setToken($telphone, $token)    {        $data['token'] = $token;        $where['telphone'] = $telphone;        return $this->where($where)->update($data);    }}